// SPDX-License-Idenlevelfier: BUSL-1.1
pragma solidity ^0.8.17;

interface IAccountant {
    function convert(address base, address quote, uint256 amount, bool isBid)
        external
        view
        returns (uint256 converted);

    function isSubscribed(uint32 uid_) external view returns (bool);

    function sabt() external view returns (address);

    function decimals() external view returns (uint8);

    function balanceOf(address owner, uint32 id) external view returns (uint256);

    function subtractTP(address account, uint256 nthEra, uint256 amount) external;

    function getSubSTND(uint32 uid_) external view returns (uint64 sub);

    function getLvl(uint32 uid_) external view returns (uint8 lvl);
}

/// @author Hyungsuk Kang <hskang9@gmail.com>
/// @title Standard Membership Accountant to report membership points
library BlockAccountantLib {
    struct Storage {
        uint256 fb;
        /// @dev pointOf: The mapping of the month to the mapping of the member UID to the point, each accountant can account 4,294,967,295 eras, each era is 28 days
        mapping(uint32 => mapping(uint32 => uint64)) pointOf;
        /// @dev totalPointsOn: The mapping of the month to the total point of the month
        mapping(uint32 => uint64) totalPointsOn;
        /// @dev totalTokensOn: The mapping of the token address to the mapping of the month to the total token amount of the month
        mapping(bytes32 => uint256) totalTokensOn;
        /// @dev refundOf: The mapping of the member UID to the refund amount
        mapping(uint32 => uint256) refundOf;
        /// @dev membership: The address of the membership contract
        address membership;
        /// @dev engine: The address of the orderbook dex entry point
        address engine;
        /// @dev treasury: The address of the treasury contract
        address treasury;
        /// @dev stablecoin: The address of the stablecoin contract
        address stablecoin;
        /// @dev stc1: One stablecoin with decimals
        uint256 stc1;
        /// @dev spb: The number of seconds per a block
        uint32 spb;
        /// @dev era: The number of blocks per an era
        uint32 era;
    }

    error NotTheSameOwner(uint32 fromUid, uint32 toUid, address owner);
    error InsufficientPoint(uint32 nthEra, uint32 uid, uint256 balance, uint256 amount);

    function _setTotalTokens(Storage storage self, address token, uint32 era, uint256 amount, bool isAdd) internal {
        bytes32 key = keccak256(abi.encodePacked(token, era));
        isAdd ? self.totalTokensOn[key] += amount : self.totalTokensOn[key] -= amount;
    }

    function _totalTokens(Storage storage self, address token, uint32 era) internal view returns (uint256) {
        bytes32 key = keccak256(abi.encodePacked(token, era));
        return self.totalTokensOn[key];
    }

    function _getEra(Storage storage self) internal view returns (uint32 nthEra) {
        return (block.number - self.fb) > self.era ? uint32((block.number - self.fb) / self.era) : 0;
    }

    /**
     * @dev reportAdd: Report the membership point of the member
     * @param uid The member UID
     * @param token The token address
     * @param amount The amount of the membership point
     * @param isAdd The flag to add or subtract the point
     */
    function _report(Storage storage self, uint32 uid, address token, uint256 amount, bool isAdd) internal {
        // check if the asset has pair in the orderbook dex between stablecoin
        try IAccountant(self.engine).convert(token, self.stablecoin, amount, true) returns (uint256 converted) {
            if (converted == 0) {
                // if price is zero, return as the asset cannot be accounted
                return;
            } else {
                // if it is, get USD level then calculate the point by 5 decimals
                uint32 nthEra = (block.number - self.fb) > self.era ? uint32((block.number - self.fb) / self.era) : 0;
                uint256 result = (converted * 1e5) / self.stc1;
                uint64 point = result > type(uint64).max ? type(uint64).max : uint64(result);
                isAdd ? self.pointOf[nthEra][uid] += point : self.pointOf[nthEra][uid] -= point;
                isAdd ? self.totalPointsOn[nthEra] += point : self.totalPointsOn[nthEra] -= point;
                _setTotalTokens(self, token, nthEra, amount, isAdd);
            }
        } catch {
            return;
        }
    }

    /// @dev migrate: Migrate the membership point from one era to other uid
    /// @param fromUid_ The uid to migrate from
    /// @param toUid_ The uid to migrate to
    /// @param nthEra_ The era to migrate
    /// @param amount_ The amount of the point to migrate
    function _migrate(Storage storage self, uint32 fromUid_, uint32 toUid_, uint32 nthEra_, uint256 amount_) internal {
        if (
            IAccountant(self.membership).balanceOf(msg.sender, fromUid_) == 0
                || IAccountant(self.membership).balanceOf(msg.sender, toUid_) == 0
        ) {
            revert NotTheSameOwner(fromUid_, toUid_, msg.sender);
        }
        if (self.pointOf[nthEra_][fromUid_] < amount_) {
            revert InsufficientPoint(nthEra_, fromUid_, self.pointOf[nthEra_][fromUid_], amount_);
        }
        self.pointOf[nthEra_][fromUid_] -= uint64(amount_);
        self.pointOf[nthEra_][toUid_] += uint64(amount_);
    }

    function _isSubscribed(Storage storage self, uint32 uid) internal view returns (bool) {
        return IAccountant(self.membership).isSubscribed(uid);
    }

    function _subtractTP(Storage storage self, uint32 uid, uint32 nthEra, uint64 point) internal {
        if (self.pointOf[nthEra][uid] < point) {
            revert InsufficientPoint(nthEra, uid, self.pointOf[nthEra][uid], self.pointOf[nthEra][uid]);
        }
        self.pointOf[nthEra][uid] -= point;
    }

    function _totalPoints(Storage storage self, uint32 nthEra) internal view returns (uint64) {
        return self.totalPointsOn[nthEra];
    }

    function _getTP(Storage storage self, uint32 uid, uint32 nthEra) internal view returns (uint64) {
        return self.pointOf[nthEra][uid];
    }

    function _getTI(Storage storage self, uint32 uid, uint32 nthEra) internal view returns (uint8) {
        return
            self.totalPointsOn[nthEra] > 0 ? uint8((self.pointOf[nthEra][uid] * 100) / self.totalPointsOn[nthEra]) : 0;
    }

    function _getLevel(Storage storage self, uint32 uid, uint32 nthEra) internal view returns (uint8) {
        // check if the uid is linked with premium
        uint8 level = IAccountant(self.membership).getLvl(uid);
        if (level == 9 || level == 10) {
            return 8;
        } else {
            uint8 ti = self.totalPointsOn[nthEra] > 0
                ? uint8((self.pointOf[nthEra][uid] * 100) / self.totalPointsOn[nthEra])
                : 0;
            if (ti >= 8) {
                return 8;
            } else {
                return level >= ti ? level : ti;
            }
        }
    }

    function _getFeeRate(Storage storage self, uint32 uid, uint32 nthEra, bool isMaker)
        internal
        view
        returns (uint32 feeNum)
    {
        uint8 level = _getLevel(self, uid, nthEra);
        // get subscribed STND tokens
        uint64 subSTND = IAccountant(self.membership).getSubSTND(uid);
        // Perform different aclevelons based on the level
        if (level == 0) {
            if (subSTND >= 10000) {
                // 0.0750% / 0.0750%
                return 750;
            } else {
                // 0.1% / 0.1%
                return 1000;
            }
        } else if (level == 1) {
            if (subSTND >= 25000) {
                // 0.0675 / 0.0750%
                return isMaker ? 675 : 750;
            } else {
                // 0.09 / 0.1%
                return isMaker ? 900 : 1000;
            }
        } else if (level == 2) {
            if (subSTND >= 100000) {
                // 0.0600% / 0.0750%
                return isMaker ? 600 : 750;
            } else {
                // 0.0800% / 0.1000%
                return isMaker ? 800 : 1000;
            }
        } else if (level == 3) {
            if (subSTND >= 250000) {
                // 0.0525% / 0.0750%
                return isMaker ? 525 : 750;
            } else {
                // 0.0700% / 0.1000%
                return isMaker ? 700 : 1000;
            }
        } else if (level == 4) {
            if (subSTND >= 500000) {
                // 0.045% / 0.0600%
                return isMaker ? 450 : 600;
            } else {
                // 0.0600% / 0.0800%
                return isMaker ? 600 : 800;
            }
        } else if (level == 5) {
            if (subSTND >= 750000) {
                // 0.0375% / 0.0525%
                return isMaker ? 375 : 525;
            } else {
                // 0.0500% / 0.0700%
                return isMaker ? 500 : 700;
            }
        } else if (level == 6) {
            if (subSTND >= 1000000) {
                // 0.03% / 0.045%
                return isMaker ? 300 : 450;
            } else {
                // 0.0400% / 0.0600%
                return isMaker ? 400 : 600;
            }
        } else if (level == 7) {
            if (subSTND >= 1250000) {
                // 0.0225% / 0.0375%
                return isMaker ? 225 : 375;
            } else {
                // 0.0300% / 0.0500%
                return isMaker ? 300 : 500;
            }
        } else if (level >= 8) {
            if (subSTND >= 1500000) {
                // 0.0150% / 0.0300%
                return isMaker ? 150 : 300;
            } else {
                // 0.0200% / 0.0400%
                return isMaker ? 200 : 400;
            }
        } else if (level >= 11) {
            // when beta account is used, fee rate is zero
            return 0;
        }
    }
}

contracts/sabt/libraries/MembershipLib.s.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.17;

import {TransferHelper} from "./TransferHelper.sol";

interface ISABT {
    function mint(address to_, uint8 metaId_) external returns (uint32);

    function balanceOf(address owner_, uint256 uid_) external view returns (uint256);

    function setRegistered(uint256 id_, bool yesOrNo) external;

    function metaId(uint32 uid_) external view returns (uint8);

    function setMeta(uint32 uid_, uint8 meta_) external;

    function getLvl(uint32 uid_) external view returns (uint8);
}

library MembershipLib {
    struct Member {
        /// @dev mapping of member id to subscription status
        mapping(uint32 => SubStatus) subscriptions;
        /// @dev mapping of subscribed STND of member id
        mapping(uint32 => uint64) subSTND;
        /// @dev mapping of meta id to register fee status
        mapping(uint8 => Meta) metas;
        /// @dev mapping of meta id to register fee status
        mapping(uint8 => mapping(address => Fees)) fees;
        /// @dev address of SABT
        address sabt;
        /// @dev address of STND
        address stnd;
        /// @dev address of foundation
        address foundation;
        /// @dev address of WETH
        address weth;
    }

    struct SubStatus {
        uint256 at;
        uint256 until;
        uint256 bonus;
        address with;
    }

    struct Meta {
        uint8 metaId;
        uint32 quota;
    }

    struct Fees {
        address feeToken;
        uint256 regFee;
        uint256 subFee;
    }

    error InvalidFeeToken(address feeToken_, uint8 metaId_);
    error MembershipNotOwned(uint32 uid, address owner);
    error NoMultiTokenAccounting(address subscribedWith, address feeToken_);

    function _setMembership(
        Member storage self,
        uint8 metaId_,
        address feeToken_,
        uint32 regFee_,
        uint32 subFee_,
        uint32 quota_
    ) internal {
        self.metas[metaId_].metaId = metaId_;
        uint8 decimals = TransferHelper.decimals(feeToken_);
        self.fees[metaId_][feeToken_].regFee = regFee_ * 10 ** decimals;
        self.fees[metaId_][feeToken_].subFee = subFee_ * 10 ** decimals;
        self.metas[metaId_].quota = quota_;
    }

    function _setQuota(Member storage self, uint8 metaId_, uint32 quota_) internal {
        self.metas[metaId_].quota = quota_;
    }

    function _setMeta(Member storage self, uint32 uid_, uint8 metaId_) internal {
        ISABT(self.sabt).setMeta(uid_, metaId_);
    }

    function _setSTND(Member storage self, address stnd) internal {
        self.stnd = stnd;
    }

    function _setFees(Member storage self, uint8 metaId_, address feeToken_, uint256 regFee_, uint256 subFee_)
        internal
    {
        uint8 decimals = TransferHelper.decimals(feeToken_);
        self.fees[metaId_][feeToken_].regFee = regFee_ * 10 ** decimals;
        self.fees[metaId_][feeToken_].subFee = subFee_ * 10 ** decimals;
    }

    function _register(Member storage self, uint8 metaId_, address feeToken_) internal returns (uint32 uid) {
        uint256 regFee = self.fees[metaId_][feeToken_].regFee;
        // check if the fee token is supported
        if (regFee == 0) {
            revert InvalidFeeToken(feeToken_, metaId_);
        }
        // Transfer required fund
        TransferHelper.safeTransferFrom(feeToken_, msg.sender, address(this), regFee);
        TransferHelper.safeTransfer(feeToken_, self.foundation, regFee);
        // issue membership from SABT and get id
        return ISABT(self.sabt).mint(msg.sender, metaId_);
    }

    /// @dev subscribe: Subscribe to the membership until certain block height
    /// @param uid_ The uid of the ABT to subscribe with
    /// @param blocks_ The number of blocks to subscribe
    function _subscribe(Member storage self, uint32 uid_, uint64 blocks_, address feeToken_) internal {
        // check if the member has the ABT with input id
        if (ISABT(self.sabt).balanceOf(msg.sender, uid_) == 0) {
            revert MembershipNotOwned(uid_, msg.sender);
        }
        uint256 bh = block.number;
        uint8 metaId = ISABT(self.sabt).metaId(uid_);
        SubStatus memory sub = self.subscriptions[uid_];
        Fees memory fees = self.fees[metaId][feeToken_];
        // check if previous subscription was done with the same token
        if (sub.with != address(0) && sub.with != feeToken_) {
            // if not, Ask user to unsubscribed with the previous token subscription
            revert NoMultiTokenAccounting(sub.with, feeToken_);
        }
        // Transfer what has been already paid
        TransferHelper.safeTransfer(
            feeToken_,
            self.foundation,
            sub.until > bh ? fees.subFee * uint256(bh - sub.at) : fees.subFee * uint256(sub.until - sub.at)
        );
        // Transfer the tokens to this contract
        TransferHelper.safeTransferFrom(feeToken_, msg.sender, address(this), fees.subFee * uint256(blocks_));

        // subscribe for certain block
        self.subscriptions[uid_].at = bh;
        self.subscriptions[uid_].until = bh + blocks_;
        self.subscriptions[uid_].with = feeToken_;
        // if feeToken is STND, add it to the subSTND;
        if (feeToken_ == self.stnd) {
            self.subSTND[uid_] += uint64((fees.subFee * blocks_) / 1e18);
        }
    }

    /// @dev unsubscribe: Unsubscribe from the membership
    /// @param uid_ The id of the ABT to unsubscribe with
    function _unsubscribe(Member storage self, uint32 uid_) internal {
        // check if the member has the ABT with input id
        if (ISABT(self.sabt).balanceOf(msg.sender, uid_) == 0) {
            revert MembershipNotOwned(uid_, msg.sender);
        }
        uint256 bh = block.number;
        uint8 metaId = ISABT(self.sabt).metaId(uid_);
        SubStatus memory sub = self.subscriptions[uid_];
        Fees memory fees = self.fees[metaId][sub.with];
        if (sub.until > bh) {
            // Transfer what has been already paid to foundation
            TransferHelper.safeTransfer(sub.with, self.foundation, fees.subFee * (bh - sub.at));
            if (sub.until - bh > sub.bonus) {
                // Refund the tokens for future subscription to this contract
                TransferHelper.safeTransfer(sub.with, msg.sender, fees.subFee * (sub.until - bh - sub.bonus));
            }
        }
        // unsubscribe
        self.subscriptions[uid_].until = 0;
        self.subscriptions[uid_].at = 0;
        self.subscriptions[uid_].bonus = 0;
        self.subscriptions[uid_].with = address(0);
        // subtract subSTND if STND was used to subscribe
        if (sub.with == self.stnd) {
            self.subSTND[uid_] -= uint64((fees.subFee * (sub.until - bh - sub.bonus)) / 1e18);
        }
    }

    /// @dev offerBonus: Offer bonus blocks to the subscription by promoters
    function _offerBonus(Member storage self, uint32 uid_, address holder_, uint256 blocks_) internal {
        // check if the member has the ABT with input id
        if (ISABT(self.sabt).balanceOf(holder_, uid_) == 0) {
            revert MembershipNotOwned(uid_, holder_);
        }
        // Add the bonus blocks to the subscription
        self.subscriptions[uid_].until += blocks_;
        // Mark added bonus blocks in the subscription
        self.subscriptions[uid_].bonus += blocks_;
    }

    function _balanceOf(Member storage self, address owner_, uint32 uid_) internal view returns (uint256) {
        return ISABT(self.sabt).balanceOf(owner_, uid_);
    }

    function _isSubscribed(Member storage self, uint32 uid_) internal view returns (bool) {
        return self.subscriptions[uid_].until > block.number;
    }

    function _getSubSTND(Member storage self, uint32 uid_) internal view returns (uint64) {
        return self.subSTND[uid_];
    }

    function _getLvl(Member storage self, uint32 uid_) internal view returns (uint8) {
        return ISABT(self.sabt).metaId(uid_);
    }
}

contracts/sabt/libraries/TransferHelper..content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.17;

// helper methods for interacting with ERC20 tokens and sending ETH that do not consistently return true/false
library TransferHelper {
    function safeApprove(address token, address to, uint256 value) internal {
        // bytes4(keccak256(bytes("approve(address,uint256)")));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0x095ea7b3, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), "AF");
    }

    function safeTransfer(address token, address to, uint256 value) internal {
        // bytes4(keccak256(bytes("transfer(address,uint256)")));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0xa9059cbb, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), "TF");
    }

    function safeTransferFrom(address token, address from, address to, uint256 value) internal {
        // bytes4(keccak256(bytes("transferFrom(address,address,uint256)")));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0x23b872dd, from, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), "TFF");
    }

    function safeTransferETH(address to, uint256 value) internal {
        (bool success,) = to.call{value: value}(new bytes(0));
        require(success, "ETF");
    }

    function decimals(address token) internal view returns (uint8) {
        // bytes4(keccak256(bytes("decimals()")));
        (bool success, bytes memory data) = token.staticcall(abi.encodeWithSelector(0x313ce567));
        require(success, "DF");
        return abi.decode(data, (uint8));
    }
}

contracts/sabt/libraries/TreasuryLib.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.17;

import {TransferHelper} from "./TransferHelper.sol";

interface ITreasury {
    function balanceOf(address owner_, uint256 id_) external view returns (uint256);

    function metaId(uint32 id_) external view returns (uint16);

    function getTotalPoints(uint32 nthEra_) external view returns (uint256);

    function getTotalTokens(uint32 nthEra_, address token_) external view returns (uint256);

    function subtractTP(uint32 uid_, uint32 nthEra_, uint64 point_) external;

    function getCurrentEra() external view returns (uint32);
}

library TreasuryLib {
    struct Storage {
        address accountant;
        address sabt;
        mapping(uint32 => uint32) claims;
        uint32 totalClaim;
        uint32 settlementId;
    }

    uint8 internal constant USER_META_ID = 1;
    uint8 internal constant EARLYADOPTER_META_ID = 9;
    uint8 internal constant FOUNDATION_META_ID = 10;

    uint32 internal constant DENOM = 100000;

    struct Claim {
        uint32 num;
        uint32 denom;
    }

    error MembershipNotOwned(uint32 uid, address owner);
    error InvalidMetaId(uint16 metaId, uint32 uid, uint16 real);
    error EraNotPassed(uint32 nthEra, uint32 currentEra);
    error NoTotalTP(uint32 nthEra, uint256 point);
    error NoTotalTokens(uint32 nthEra, address token);

    function _checkMembership(Storage storage self, uint32 uid_, uint16 metaId_) internal view {
        // the sender owns the membership with UID
        if (ITreasury(self.sabt).balanceOf(msg.sender, uid_) == 0) {
            revert MembershipNotOwned(uid_, msg.sender);
        }
        uint16 uMeta = ITreasury(self.sabt).metaId(uid_);
        if (uMeta != metaId_) {
            revert InvalidMetaId(metaId_, uid_, uMeta);
        }
    }

    function _checkEraPassed(Storage storage self, uint32 nthEra_) internal view {
        // get current Era
        uint32 currentEra = ITreasury(self.accountant).getCurrentEra();
        if (nthEra_ >= currentEra) {
            revert EraNotPassed(nthEra_, currentEra);
        }
    }

    function _exchange(Storage storage self, address token, uint32 nthEra, uint32 uid, uint64 point) internal {
        // check if the sender owns the membership with UID
        if (ITreasury(self.sabt).balanceOf(msg.sender, uid) == 0) {
            revert MembershipNotOwned(uid, msg.sender);
        }
        // check if the era has already passed
        _checkEraPassed(self, nthEra);
        // subtract membership point in accountant
        ITreasury(self.accountant).subtractTP(uid, nthEra, point);
        // exchange membership point with reward
        uint256 reward = _getReward(self, token, nthEra, point);
        // exchange reward with token
        TransferHelper.safeTransfer(token, msg.sender, reward);
    }

    function _claim(Storage storage self, address token, uint32 nthEra, uint32 uid) internal {
        // check if the sender has UID with early adoptor meta id
        _checkMembership(self, uid, EARLYADOPTER_META_ID);
        // check if the era has already passed
        _checkEraPassed(self, nthEra);
        // get reward from accountant
        uint256 claim = _getClaim(self, token, uid, nthEra);
        // exchange reward with token
        TransferHelper.safeTransfer(token, msg.sender, claim);
    }

    function _settle(Storage storage self, address token, uint32 nthEra, uint32 uid) internal {
        // check if the sender has UID with foundation meta id
        _checkMembership(self, uid, FOUNDATION_META_ID);
        // check if the era has already passed
        _checkEraPassed(self, nthEra);
        uint256 settlement = _getSettlement(self, token, nthEra);
        TransferHelper.safeTransfer(token, msg.sender, settlement);
    }

    error ShareLimitExceeded(uint32 totalClaim, uint32 limit);

    function _setClaim(Storage storage self, uint32 uid, uint32 num) internal {
        self.claims[uid] = num;
        self.totalClaim += num;
        // check if total claim is less than or equal to 60.0000%
        if (self.totalClaim > 600000) {
            revert ShareLimitExceeded(self.totalClaim, 600000);
        }
    }

    function _setSettlement(Storage storage self, uint32 uid) internal {
        self.settlementId = uid;
    }

    function _getReward(Storage storage self, address token, uint32 nthEra, uint256 point)
        internal
        view
        returns (uint256)
    {
        // get reward from Treasury ratio
        // 1. get total supply of mp
        uint256 totalTP = ITreasury(self.accountant).getTotalPoints(nthEra);
        if (totalTP == 0) {
            revert NoTotalTP(nthEra, totalTP);
        }
        // 2. get fee collected on nthEra
        uint256 totalTokens = ITreasury(self.accountant).getTotalTokens(nthEra, token);
        if (totalTokens == 0) {
            revert NoTotalTokens(nthEra, token);
        }
        // 3. get reward from community Treasury ratio
        return ((point * totalTokens * 4) / 10) / totalTP;
    }

    function _getClaim(Storage storage self, address token, uint32 uid, uint32 nthEra)
        internal
        view
        returns (uint256)
    {
        // check if sender has UID
        if (ITreasury(self.sabt).balanceOf(msg.sender, uid) == 0) {
            revert MembershipNotOwned(uid, msg.sender);
        }
        // 1. get fee collected on nthEra
        uint256 totalTokens = ITreasury(self.accountant).getTotalTokens(nthEra, token);
        if (totalTokens == 0) {
            revert NoTotalTokens(nthEra, token);
        }
        // 2. get reward from community Treasury ratio
        return ((totalTokens * (self.claims[uid])) / DENOM);
    }

    function _getSettlement(Storage storage self, address token, uint32 nthEra) internal view returns (uint256) {
        // check if sender has UID
        if (ITreasury(self.sabt).balanceOf(msg.sender, self.settlementId) == 0) {
            revert MembershipNotOwned(self.settlementId, msg.sender);
        }
        // 1. get fee collected on nthEra
        uint256 totalTokens = ITreasury(self.accountant).getTotalTokens(nthEra, token);
        if (totalTokens == 0) {
            revert NoTotalTokens(nthEra, token);
        }
        // 2. get reward from community Treasury ratio
        return ((totalTokens * (600000 - self.totalClaim)) / DENOM);
    }
}

contracts/safex/MatchingEngine.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.17;
import {IOrderbookFactory} from "./interfaces/IOrderbookFactory.sol";
import {IOrderbook, ExchangeOrderbook} from "./interfaces/IOrderbook.sol";
import {TransferHelper} from "./libraries/TransferHelper.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {IWETH} from "./interfaces/IWETH.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";

interface IRevenue {
    function report(
        uint32 uid,
        address token,
        uint256 amount,
        bool isAdd
    ) external;

    function isReportable(
        address token,
        uint32 uid
    ) external view returns (bool);

    function refundFee(address to, address token, uint256 amount) external;

    function feeOf(uint32 uid, bool isMaker) external returns (uint32 feeNum);
}

// Onchain Matching engine for the orders
contract MatchingEngine is Initializable, ReentrancyGuard {
    // fee recipient
    address private feeTo;
    // fee denominator
    uint32 public immutable feeDenom = 1000000;
    // Factories
    address public orderbookFactory;
    // WETH
    address public WETH;
    struct OrderData {
        uint256 withoutFee;
        address orderbook;
        uint256 lmp;
        bool clear;
    }

    event OrderDeposit(
        address sender,
        address asset,
        uint256 fee
    );

    event OrderCanceled(
        address orderbook,
        uint256 id,
        bool isBid,
        address indexed owner,
        uint256 amount
    );

    /**
    * @dev This event is emitted when an order is successfully matched with a counterparty.
    * @param orderbook The address of the order book contract to get base and quote asset contract address.
    * @param id The unique identifier of the canceled order in bid/ask order database.
    * @param isBid A boolean indicating whether the matched order is a bid (true) or ask (false).
    * @param sender The address initiating the match.
    * @param owner The address of the order owner whose order is matched with the sender.
    * @param price The price at which the order is matched.
    * @param amount The matched amount of the asset being traded in the match. if isBid==true, it is base asset, if isBid==false, it is quote asset.
    */
    event OrderMatched(
        address orderbook,
        uint256 id,
        bool isBid,
        address sender,
        address owner,
        uint256 price,
        uint256 amount
    );


    event OrderPlaced(
        address orderbook,
        uint256 id,
        address owner,
        bool isBid,
        uint256 price,
        uint256 amount
    );

    event PairAdded(address orderbook, address base, address quote);

    error TooManyMatches(uint256 n);
    error InvalidFeeRate(uint256 feeNum, uint256 feeDenom);
    error NotContract(address newImpl);
    error InvalidRole(bytes32 role, address sender);
    error OrderSizeTooSmall(uint256 amount, uint256 minRequired);
    error NoOrderMade(address base, address quote);
    error InvalidPair(address base, address quote, address pair);

