// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.17;

library ExchangeOrderbook {
  // Order struct
  struct Order {
    address owner;
    uint256 price;
    uint256 depositAmount;
  }

  // Order Linked List
  struct OrderStorage {
    /// Hashmap-style linked list of prices to route orders
    // key: price, value: order indices linked hashmap
    mapping(uint256 => mapping(uint32 => uint32)) list;
    mapping(uint32 => Order) orders;
    mapping(uint32 => bool) canceled;
    // Head of the linked list(i.e. lowest ask price / highest bid price)
    mapping(uint256 => uint32) head;
    // count of the orders, used for array allocation
    uint32 count;
    address engine;
  }

  error OrderIdIsZero(uint32 id);

  // for orders, lower depositAmount are next, higher depositAmount comes first
  function _insertId(
    OrderStorage storage self,
    uint256 price,
    uint32 id,
    uint256 amount
  ) internal {
    uint32 last = 0;
    uint32 head = self.head[price];
    mapping(uint32 => uint32) storage list = self.list[price];
    mapping(uint32 => Order) storage orders = self.orders;
    // insert order to the linked list
    // if the list is empty
    if (head == 0 || amount > self.orders[head].depositAmount) {
      self.head[price] = id;
      list[id] = head;
      return;
    }
    // Traverse through list until we find the right spot where id's deposit amount is higher than next
    while (head != 0) {
      // what if order deposit amount is bigger than the next order's deposit amount?
      uint32 next = list[head];
      if (amount < orders[next].depositAmount) {
        // Keep traversing
        head = list[head];
        last = next;
      } else if (amount > orders[next].depositAmount) {
        // This is either order is cancelled or order is at the end of the list
        if (orders[next].depositAmount == 0) {
          // what if next order is canceled and order id still stays in the list?
          // if order is canceled and stays in the list, there is at least a next order in the list
          if (self.canceled[next]) {
            // remove index of cancelled order
            list[head] = list[next];
            delete list[next];
            delete self.canceled[next];
            // skip canceled order
            last = head;
            head = list[head];
          } else {
            // Insert order at the end of the list
            list[head] = id;
            list[id] = 0;
            return;
          }
        }
        // Insert order in the middle of the list
        list[head] = id;
        list[id] = next;
        return;
      }
      // what if there is same order with same deposit amount?
      else if (amount == orders[next].depositAmount) {
        list[id] = list[next];
        list[next] = id;
        return;
      }
    }
  }

  // pop front
  function _fpop(
    OrderStorage storage self,
    uint256 price
  ) internal returns (uint256) {
    uint32 first = self.head[price];
    if (first == 0) {
      return 0;
    }
    uint32 next = self.list[price][first];
    self.head[price] = next;
    delete self.list[price][first];
    return first;
  }

  function _createOrder(
    OrderStorage storage self,
    address owner,
    uint256 price,
    uint256 depositAmount
  ) internal returns (uint32 id) {
    Order memory order = Order({ owner: owner, price: price, depositAmount: depositAmount });
    // prevent order overflow, order id must start from 1
    self.count = self.count == 0 || self.count == type(uint32).max
      ? 1
      : self.count + 1;
    self.orders[self.count] = order;
    return self.count;
  }

  function _decreaseOrder(
    OrderStorage storage self,
    uint32 id,
    uint256 amount,
    uint256 dust
  ) internal returns (uint256) {
    uint256 decreased = self.orders[id].depositAmount - amount;
    // remove dust
    if (decreased <= dust || amount == 0) {
      decreased = self.orders[id].depositAmount;
      _deleteOrder(self, id);
      return decreased;
    } else {
      self.orders[id].depositAmount = decreased;
      return amount;
    }
  }

  function _deleteOrder(
    OrderStorage storage self,
    uint32 id
  ) internal {
    uint256 price = self.orders[id].price;
    uint32 last = 0;
    uint32 head = self.head[price];
    uint32 next;
    uint16 i;
    mapping(uint32 => uint32) storage list = self.list[price];
    // delete id in the order linked list
    if (head == id) {
      self.head[price] = list[head];
      delete list[id];
    } else {
      // search for the order id in the linked list
      while (head != 0) {
        next = list[head];
        if (next == id) {
          list[head] = list[next];
          delete list[id];
          break;
        }
        last = head;
        head = next;
        ++i;
      }
    }
    // delete order
    delete self.orders[id];
    return;
  }

  // show n order ids at the price in the orderbook
  function _getOrderIds(
    OrderStorage storage self,
    uint256 price,
    uint32 n
  ) internal view returns (uint32[] memory) {
    uint32 head = self.head[price];
    uint32[] memory orders = new uint32[](n);
    uint32 i = 0;
    while (head != 0 && i < n) {
      orders[i] = head;
      head = self.list[price][head];
      i++;
    }
    return orders;
  }

  function _getOrders(
    OrderStorage storage self,
    uint256 price,
    uint32 n
  ) internal view returns (Order[] memory) {
    uint32 head = self.head[price];
    Order[] memory orders = new Order[](n);
    uint32 i = 0;
    while (head != 0 && i < n) {
      orders[i] = self.orders[head];
      head = self.list[price][head];
      i++;
    }
    return orders;
  }

  function _head(
    OrderStorage storage self,
    uint256 price
  ) internal view returns (uint32) {
    return self.head[price];
  }

  function _isEmpty(
    OrderStorage storage self,
    uint256 price
  ) internal view returns (bool) {
    return self.head[price] == 0;
  }

  function _next(
    OrderStorage storage self,
    uint256 price,
    uint32 curr
  ) internal view returns (uint32) {
    return self.list[price][curr];
  }

  function _getOrder(
    OrderStorage storage self,
    uint32 id
  ) internal view returns (Order memory) {
    if(id == 0) {
      revert OrderIdIsZero(id);
    }
    return self.orders[id];
  }
}

contracts/safex/libraries/TransferHelper.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.17;

// helper methods for interacting with ERC20 tokens and sending ETH that do not consistently return true/false
library TransferHelper {
    function safeApprove(address token, address to, uint256 value) internal {
        // bytes4(keccak256(bytes("approve(address,uint256)")));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0x095ea7b3, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), "AF");
    }

    function safeTransfer(address token, address to, uint256 value) internal {
        // bytes4(keccak256(bytes("transfer(address,uint256)")));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0xa9059cbb, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), "TF");
    }

    function safeTransferFrom(address token, address from, address to, uint256 value) internal {
        // bytes4(keccak256(bytes("transferFrom(address,address,uint256)")));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0x23b872dd, from, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), "TFF");
    }

    function safeTransferETH(address to, uint256 value) internal {
        (bool success,) = to.call{value: value}(new bytes(0));
        require(success, "ETF");
    }

    function decimals(address token) internal view returns (uint8) {
        // bytes4(keccak256(bytes("decimals()")));
        (bool success, bytes memory data) = token.staticcall(abi.encodeWithSelector(0x313ce567));
        require(success, "DF");
        return abi.decode(data, (uint8));
    }
}

contracts/safex/orderbooks/Orderbook.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.17;

import {IOrderbook} from "../interfaces/IOrderbook.sol";
import {Initializable} from "../../security/Initializable.sol";
import {TransferHelper} from "../libraries/TransferHelper.sol";
import {ExchangeLinkedList} from "../libraries/ExchangeLinkedList.sol";
import {ExchangeOrderbook} from "../libraries/ExchangeOrderbook.sol";

interface IWETHMinimal {
    function WETH() external view returns (address);

    function transfer(address to, uint256 value) external returns (bool);

    function withdraw(uint256) external;
}

contract Orderbook is IOrderbook, Initializable {
    using ExchangeLinkedList for ExchangeLinkedList.PriceLinkedList;
    using ExchangeOrderbook for ExchangeOrderbook.OrderStorage;

    // Pair Struct
    struct Pair {
        uint256 id;
        address base;
        address quote;
        address engine;
    }

    Pair private pair;

    uint64 private decDiff;
    bool private baseBquote;

    ExchangeLinkedList.PriceLinkedList private priceLists;
    ExchangeOrderbook.OrderStorage private _askOrders;
    ExchangeOrderbook.OrderStorage private _bidOrders;

    error InvalidDecimals(uint8 base, uint8 quote);
    error InvalidAccess(address sender, address allowed);
    error OrderSizeTooSmall(uint256 amount, uint256 minRequired);

    function initialize(
        uint256 id_,
        address base_,
        address quote_,
        address engine_
    ) external initializer {
        uint8 baseD = TransferHelper.decimals(base_);
        uint8 quoteD = TransferHelper.decimals(quote_);
        if (baseD > 18 || quoteD > 18) {
            revert InvalidDecimals(baseD, quoteD);
        }
        (uint8 diff, bool baseBquote_) = _absdiff(baseD, quoteD);
        decDiff = uint64(10 ** diff);
        baseBquote = baseBquote_;
        pair = Pair(id_, base_, quote_, engine_);
    }

    modifier onlyEngine() {
        if (msg.sender != pair.engine) {
            revert InvalidAccess(msg.sender, pair.engine);
        }
        _;
    }

    function setLmp(uint256 price) external onlyEngine {
        priceLists._setLmp(price);
    }

    function placeAsk(
        address owner,
        uint256 price,
        uint256 amount
    ) external onlyEngine returns (uint32 id) {
        id = _askOrders._createOrder(owner, price, amount);
        // check if the price is new in the list. if not, insert id to the list
        if (_askOrders._isEmpty(price)) {
            priceLists._insert(false, price);
        }
        _askOrders._insertId(price, id, amount);
        return id;
    }

    function placeBid(
        address owner,
        uint256 price,
        uint256 amount
    ) external onlyEngine returns (uint32 id) {
        id = _bidOrders._createOrder(owner, price, amount);
        // check if the price is new in the list. if not, insert id to the list
        if (_bidOrders._isEmpty(price)) {
            priceLists._insert(true, price);
        }
        _bidOrders._insertId(price, id, amount);
        return id;
    }

    function cancelOrder(
        bool isBid,
        uint32 orderId,
        address owner
    ) external onlyEngine returns (uint256 remaining) {
        // check order owner
        ExchangeOrderbook.Order memory order = isBid
            ? _bidOrders._getOrder(orderId)
            : _askOrders._getOrder(orderId);

        // check before the price had an order not being empty
        bool wasEmpty = isEmpty(isBid, order.price);

        if (order.owner != owner) {
            revert InvalidAccess(owner, order.owner);
        }

        isBid
            ? _bidOrders._deleteOrder(orderId)
            : _askOrders._deleteOrder(orderId);
        isBid
            ? _sendFunds(pair.quote, owner, order.depositAmount)
            : _sendFunds(pair.base, owner, order.depositAmount);

        // check if the canceled order was the last order in the list
        if (!wasEmpty && isEmpty(isBid, order.price)) {
            priceLists._delete(isBid, order.price);
        }

        return (order.depositAmount);
    }

    function execute(
        uint32 orderId,
        bool isBid,
        address sender,
        uint256 amount
    ) external onlyEngine returns (address owner) {
        ExchangeOrderbook.Order memory order = isBid
            ? _bidOrders._getOrder(orderId)
            : _askOrders._getOrder(orderId);
        uint256 converted = convert(order.price, amount, isBid);
        uint256 dust = convert(order.price, 1, isBid);
        // if isBid == true, sender is matching ask order with bid order(i.e. selling base to receive quote), otherwise sender is matching bid order with ask order(i.e. buying base with quote)
        if (isBid) {
            // decrease remaining amount of order
            uint256 withDust = _bidOrders._decreaseOrder(
                orderId,
                converted,
                dust
            );
            // sender is matching ask order for base asset with quote asset
            _sendFunds(pair.base, order.owner, amount);
            // send converted amount of quote asset from owner to sender
            _sendFunds(pair.quote, sender, withDust);
        }
        // if the order is bid order on the base/quote pair
        else {
            // decrease remaining amount of order
            uint256 withDust = _askOrders._decreaseOrder(
                orderId,
                converted,
                dust
            );
            // sender is matching bid order for quote asset with base asset
            // send deposited amount of quote asset from sender to owner
            _sendFunds(pair.quote, order.owner, amount);
            // send converted amount of base asset from owner to sender
            _sendFunds(pair.base, sender, withDust);
        }
        return order.owner;
    }

    function clearEmptyHead(bool isBid) external returns (uint256 head) {
        head = isBid ? priceLists._bidHead() : priceLists._askHead();
        uint32 orderId = isBid
            ? _bidOrders._head(head)
            : _askOrders._head(head);
        while (orderId == 0 && head != 0) {
            orderId = isBid ? _bidOrders._head(head) : _askOrders._head(head);
            if (orderId == 0) {
                head = priceLists._clearHead(isBid);
            }
        }
        return head;
    }

    function fpop(
        bool isBid,
        uint256 price,
        uint256 remaining
    ) external onlyEngine returns (uint32 orderId, uint256 required) {
        orderId = isBid ? _bidOrders._head(price) : _askOrders._head(price);
        ExchangeOrderbook.Order memory order = isBid
            ? _bidOrders._getOrder(orderId)
            : _askOrders._getOrder(orderId);
        required = convert(price, order.depositAmount, !isBid);
        if (required <= remaining) {
            isBid ? _bidOrders._fpop(price) : _askOrders._fpop(price);
            if (isEmpty(isBid, price)) {
                isBid
                    ? priceLists.bidHead = priceLists._next(isBid, price)
                    : priceLists.askHead = priceLists._next(isBid, price);
            }
        }
        return (orderId, required);
    }

    function _sendFunds(
        address token,
        address to,
        uint256 amount
    ) internal returns (bool) {
        address weth = IWETHMinimal(pair.engine).WETH();
        if (token == weth) {
            IWETHMinimal(weth).withdraw(amount);
            return payable(to).send(amount);
        } else {
            TransferHelper.safeTransfer(token, to, amount);
            return true;
        }
    }

    function _absdiff(uint8 a, uint8 b) internal pure returns (uint8, bool) {
        return (a > b ? a - b : b - a, a > b);
    }

    // get required amount for executing the order
    function getRequired(
        bool isBid,
        uint256 price,
        uint32 orderId
    ) external view returns (uint256 required) {
        ExchangeOrderbook.Order memory order = isBid
            ? _bidOrders._getOrder(orderId)
            : _askOrders._getOrder(orderId);
        if (order.depositAmount == 0) {
            return 0;
        }
        /* if ask, required base amount is quoteAmount / price,
         * converting the number converting decimal from quote to base,
         * otherwise quote amount is baseAmount * price, converting decimal from base to quote
         */
        return convert(price, order.depositAmount, isBid);
    }

    /////////////////////////////////
    /// Price linked list methods ///
    /////////////////////////////////

    function heads() external view returns (uint256, uint256) {
        return priceLists._heads();
    }

    function askHead() external view returns (uint256) {
        return priceLists._askHead();
    }

    function bidHead() external view returns (uint256) {
        return priceLists._bidHead();
    }

    function mktPrice() external view returns (uint256) {
        return priceLists._mktPrice();
    }

    function getPrices(
        bool isBid,
        uint32 n
    ) external view returns (uint256[] memory) {
        return priceLists._getPrices(isBid, n);
    }

    function getOrderIds(
        bool isBid,
        uint256 price,
        uint32 n
    ) external view returns (uint32[] memory) {
        return
            isBid
                ? _bidOrders._getOrderIds(price, n)
                : _askOrders._getOrderIds(price, n);
    }

    function getOrders(
        bool isBid,
        uint256 price,
        uint32 n
    ) external view returns (ExchangeOrderbook.Order[] memory) {
        return
            isBid
                ? _bidOrders._getOrders(price, n)
                : _askOrders._getOrders(price, n);
    }

    function getOrder(
        bool isBid,
        uint32 orderId
    ) external view returns (ExchangeOrderbook.Order memory) {
        return
            isBid
                ? _bidOrders._getOrder(orderId)
                : _askOrders._getOrder(orderId);
    }

    function getBaseQuote()
        external
        view
        returns (address base, address quote)
    {
        return (pair.base, pair.quote);
    }

    /**
     * @dev get asset value in quote asset if isBid is true, otherwise get asset value in base asset
     * @param amount amount of asset in base asset if isBid is true, otherwise in quote asset
     * @param isBid if true, get asset value in quote asset, otherwise get asset value in base asset
     * @return converted asset value in quote asset if isBid is true, otherwise asset value in base asset
     */
    function assetValue(
        uint256 amount,
        bool isBid
    ) external view returns (uint256 converted) {
        return convert(priceLists._mktPrice(), amount, isBid);
    }

    function isEmpty(bool isBid, uint256 price) public view returns (bool) {
        return isBid ? _bidOrders._isEmpty(price) : _askOrders._isEmpty(price);
    }

    function convert(
        uint256 price,
        uint256 amount,
        bool isBid
    ) public view returns (uint256 converted) {
        if (isBid) {
            // convert quote to base
            return
                baseBquote
                    ? ((amount * price) / 1e8) / decDiff
                    : ((amount * price) / 1e8) * decDiff;
        } else {
            // convert base to quote
            return
                baseBquote
                    ? ((amount * 1e8) / price) * decDiff
                    : ((amount * 1e8) / price) / decDiff;
        }
    }

    receive() external payable {
        assert(msg.sender == IWETHMinimal(pair.engine).WETH());
    }
}

contracts/safex/orderbooks/OrderbookFact.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.17;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {Orderbook, IOrderbook} from "./Orderbook.sol";
import {CloneFactory} from "../libraries/CloneFactory.sol";
import {IOrderbookFactory} from "../interfaces/IOrderbookFactory.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

interface IERC20 {
    function symbol() external view returns (string memory);
}

contract OrderbookFactory is IOrderbookFactory, Initializable {
    // Orderbooks
    address[] public allPairs;
    /// Address of manager
    address public override engine;
    /// version number of impl
    uint32 public version;
    /// address of order impl
    address public impl;

    error InvalidAccess(address sender, address allowed);
    error PairAlreadyExists(address base, address quote, address pair);
    error SameBaseQuote(address base, address quote);

    constructor() {
    }

    function createBook(
        address base_,
        address quote_
    ) external override returns (address orderbook) {
        if (msg.sender != engine) {
            revert InvalidAccess(msg.sender, engine);
        }

        if (base_ == quote_) {
            revert SameBaseQuote(base_, quote_);
        }

        address pair = _predictAddress(base_, quote_);

        // Check if the address has code
        uint32 size;
        assembly {
            size := extcodesize(pair)
        }

        // If the address has code and it's a clone of impl, revert.
        if (size > 0 || CloneFactory._isClone(impl, pair)) {
            revert PairAlreadyExists(base_, quote_, pair);
        }

        address proxy = CloneFactory._createCloneWithSalt(
            impl,
            _getSalt(base_, quote_)
        );
        IOrderbook(proxy).initialize(
            allPairsLength(),
            base_,
            quote_,
            engine
        );
        allPairs.push(proxy);
        return (proxy);
    }

    function isClone(address vault) external view returns (bool cloned) {
        cloned = CloneFactory._isClone(impl, vault);
    }

    function getBook(uint256 bookId_) external view override returns (address) {
        return allPairs[bookId_];
    }

    function getPair(
        address base,
        address quote
    ) external view override returns (address book) {
        book = _predictAddress(base, quote);
        return address(book).code.length > 0 ? book : address(0);
    }

    function getPairs(
        uint256 start,
        uint256 end
    ) public view override returns (IOrderbookFactory.Pair[] memory) {
        uint256 last = end > allPairs.length ? allPairs.length : end;
        IOrderbookFactory.Pair[] memory pairs = new IOrderbookFactory.Pair[](
            last - start
        );
        for (uint256 i = start; i < last; i++) {
            (address base, address quote) = IOrderbook(allPairs[i])
                .getBaseQuote();
            pairs[i] = Pair(base, quote);
        }
        return pairs;
    }

    function getPairsWithIds(
        uint256[] memory ids
    ) public view override returns (IOrderbookFactory.Pair[] memory pairs) {
        pairs = new IOrderbookFactory.Pair[](ids.length);
        for (uint256 i = 0; i < ids.length; i++) {
            (address base, address quote) = IOrderbook(allPairs[i])
                .getBaseQuote();
            pairs[i] = Pair(base, quote);
        }
        return pairs;
    }

    function getPairNames(
        uint256 start,
        uint256 end
    ) external view override returns (string[] memory names) {
        IOrderbookFactory.Pair[] memory pairs = getPairs(start, end);
        names = new string[](pairs.length);
        for (uint256 i = 0; i < pairs.length; i++) {
            string memory baseName = IERC20(pairs[i].base).symbol();
            string memory quoteName = IERC20(pairs[i].quote).symbol();
            names[i] = string(abi.encodePacked(baseName, "/", quoteName));
        }
        return names;
    }

    function getPairNamesWithIds(
        uint256[] memory ids
    ) external view override returns (string[] memory names) {
        names = new string[](ids.length);
        for (uint256 i = 0; i < ids.length; i++) {
            (address base, address quote) = IOrderbook(allPairs[i])
                .getBaseQuote();
            string memory baseName = IERC20(base).symbol();
            string memory quoteName = IERC20(quote).symbol();
            names[i] = string(abi.encodePacked(baseName, "/", quoteName));
        }
        return names;
    }

    function getBaseQuote(
        address orderbook
    ) external view override returns (address base, address quote) {
        return IOrderbook(orderbook).getBaseQuote();
    }

    /**
     * @dev Initialize orderbook factory contract with engine address, reinitialize if engine is reset.
     * @param engine_ The address of the engine contract
     */
    function initialize(address engine_) public initializer {
        engine = engine_;
        _createImpl();
    }

    function allPairsLength() public view returns (uint256) {
        return allPairs.length;
    }

    // Set immutable, consistant, one rule for orderbook implementation
    function _createImpl() internal {
        address addr;
        bytes memory bytecode = type(Orderbook).creationCode;
        bytes32 salt = keccak256(abi.encodePacked("orderbook", version));
        assembly {
            addr := create2(0, add(bytecode, 0x20), mload(bytecode), salt)
            if iszero(extcodesize(addr)) {
                revert(0, 0)
            }
        }
        impl = addr;
    }

    function _predictAddress(
        address base_,
        address quote_
    ) internal view returns (address) {
        bytes32 salt = _getSalt(base_, quote_);
        return CloneFactory.predictAddressWithSalt(address(this), impl, salt);
    }

    function _getSalt(
        address base_,
        address quote_
    ) internal pure returns (bytes32) {
        return keccak256(abi.encodePacked(base_, quote_));
    }
}

contracts/safu/Bond.sol.content:
// SPDX-License-Identifier: Apache-2.0

pragma solidity ^0.8.0;

import "./interfaces/IERC20Minimal.sol";
import "./libraries/TransferHelper.sol";
import "./interfaces/IBond.sol";
import "./interfaces/INetworkState.sol";
import "./interfaces/IERC721Minimal.sol";
import "./interfaces/ICoupon.sol";
import "./interfaces/IWETH.sol";
import "./interfaces/IEngine.sol";
import "./interfaces/ISAFU.sol";

contract Bond {
    /// Address of the state
    address public state;
    /// Address of debt;
    address public debt;
    /// Address of vault ownership registry
    address public coupon;
    /// Address of a collateral
    address public collateral;
    /// Bond global identifier
    uint128 public id;
    /// Borrowed amount
    uint256 public borrow;
    /// Created block timestamp
    uint256 public createdAt;

    constructor() public {
        state = msg.sender;
        createdAt = block.timestamp;
    }

    error NotBondOwner(address caller, address owner);
    error InvalidAccess(address caller, address gov);

    modifier onlyBondOwner() {
        address owner = IERC721Minimal(coupon).ownerOf(id);
        if (owner != msg.sender) {
            revert NotBondOwner(msg.sender, owner);
        }
        _;
    }

    modifier onlyState() {
        if (state != msg.sender) {
            revert InvalidAccess(msg.sender, state);
        }
        _;
    }

    // called once by the factory at time of deployment
    function initialize(
        uint128 vaultId_,
        address collateral_,
        address debt_,
        address coupon_,
        uint256 amount_
    ) external onlyState {
        id = vaultId_;
        collateral = collateral_;
        debt = debt_;
        coupon = coupon_;
        borrow = amount_;
    }

    /// liquidate
    function liquidate() external {
        require(
            !INetworkState(state).isValidCDP(
                collateral,
                debt,
                IERC20Minimal(collateral).balanceOf(address(this)),
                IERC20Minimal(debt).balanceOf(address(this))
            ),
            "Vault: Position is still safe"
        );
        // check the pair if it exists
        address market = INetworkState(state).market();
        address pair = IEngine(market).getPair(collateral, debt);
        require(pair != address(0), "Vault: Liquidating pair not supported");
        uint256 balance = IERC20Minimal(collateral).balanceOf(address(this));
        uint256 lfr = INetworkState(state).getLFR(collateral);
        uint256 liquidationFee = (lfr * balance) / 100;
        uint256 left = _sendFee(collateral, balance, liquidationFee);
        // Distribute collaterals
        IEngine(market).marketSell(collateral, debt, left, true, 10, 0);
        // burn vault nft
        _burnV1FromVault();
        //emit Liquidated(address(this), collateral, balance);
        // self destruct the contract, send remaining balance if collateral is native currency
        selfdestruct(payable(msg.sender));
    }

    /// Deposit collateral
    function depositCollateral(uint256 amount_) external onlyBondOwner {
        TransferHelper.safeTransferFrom(
            collateral,
            msg.sender,
            address(this),
            amount_
        );
    }

    /// Withdraw collateral
    function withdrawCollateral(uint256 amount_) external onlyBondOwner {
        require(
            IERC20Minimal(collateral).balanceOf(address(this)) >= amount_,
            "Vault: Not enough collateral"
        );
        if (borrow != 0) {
            require(
                INetworkState(state).isValidCDP(
                    collateral,
                    debt,
                    IERC20Minimal(collateral).balanceOf(address(this)) -
                        amount_,
                    borrow
                ),
                "Vault: below MCR"
            );
        }
        TransferHelper.safeTransfer(collateral, msg.sender, amount_);
    }

    /// Payback debt
    function payDebt(uint256 amount_) external onlyBondOwner {
        // calculate debt with interest
        uint256 fee = _calculateFee();
        require(amount_ != 0, "Vault: amount is zero");
        // send M1 to the vault
        TransferHelper.safeTransferFrom(
            debt,
            msg.sender,
            address(this),
            amount_
        );
        uint256 left = _sendFee(debt, amount_, fee);
        _burnM1FromVault(left);
        borrow -= left;
        //emit PayBack(vaultId, borrow, fee);
    }

    /// Close CDP
    function redeemBond(uint256 amount_) external onlyBondOwner {
        // calculate debt with interest
        uint256 fee = _calculateFee();
        require(
            fee + borrow == amount_,
            "Vault: not enough balance to payback"
        );
        // send M1 to the vault
        TransferHelper.safeTransferFrom(
            debt,
            msg.sender,
            address(this),
            amount_
        );
        // send fee to the pool
        uint256 left = _sendFee(debt, amount_, fee);
        // burn M1 debt with interest
        _burnM1FromVault(left);
        // burn vault nft
        _burnV1FromVault();
        //emit CloseVault(address(this), amount_, fee);
        // self destruct the contract, send remaining balance if collateral is native currency
        selfdestruct(payable(msg.sender));
    }

    /// get amount left to borrow
    function getMargin(uint256 mcr) external view returns (uint256 available) {
        return
            ((IERC20Minimal(collateral).balanceOf(address(this)) / mcr) *
                100000) - borrow;
    }

    /// burn vault v1
    function _burnV1FromVault() internal {
        ICoupon(coupon).burnFromVault(id);
    }

    /// burn vault M1
    function _burnM1FromVault(uint256 amount_) internal {
        ISAFU(debt).burnFrom(msg.sender, amount_);
    }

    function _calculateFee() internal returns (uint256) {
        uint256 assetValue = INetworkState(state).getAssetValue(debt, borrow);
        uint256 sfr = INetworkState(state).getSFR(collateral);
        /// (sfr * assetValue/100) * (duration in months)
        uint256 sfrTimesV = sfr * assetValue;
        // get duration in months
        uint256 duration = (block.timestamp - createdAt) / 60 / 60 / 24 / 30;
        require(sfrTimesV >= assetValue); // overflow check
        return (sfrTimesV / 100) * duration;
    }

    function getDebt() external returns (uint256) {
        return _calculateFee() + borrow;
    }

    function _sendFee(
        address asset_,
        uint256 amount_,
        uint256 fee_
    ) internal returns (uint256 left) {
        address dividend = INetworkState(state).dividend();
        address feeTo = INetworkState(state).feeTo();
        address treasury = INetworkState(state).treasury();
        bool feeOn = feeTo != address(0);
        bool treasuryOn = treasury != address(0);
        bool dividendOn = dividend != address(0);
        // send fee to the pool
        if (feeOn) {
            if (dividendOn) {
                uint256 half = fee_ / 2;
                TransferHelper.safeTransfer(asset_, dividend, half);
                TransferHelper.safeTransfer(asset_, feeTo, half);
            } else if (dividendOn && treasuryOn) {
                uint256 third = fee_ / 3;
                TransferHelper.safeTransfer(asset_, dividend, third);
                TransferHelper.safeTransfer(asset_, feeTo, third);
                TransferHelper.safeTransfer(asset_, treasury, third);
            } else {
                TransferHelper.safeTransfer(asset_, feeTo, fee_);
            }
        }
        return amount_ - fee_;
    }
}

contracts/safu/Coupon.sol.content:
// SPDX-License-Identifier: Apache-2.0

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./interfaces/ICoupon.sol";
import "./interfaces/INetworkState.sol";

contract Coupon is ERC721, AccessControl  {
     // Create a new role identifier for the minter role
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");

    address manager;

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC721, AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    constructor(address manager_)
    ERC721("SAFU BOND COUPON", "COUPON") {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());

        _setupRole(MINTER_ROLE, _msgSender());
        _setupRole(MINTER_ROLE, manager);
        _setupRole(BURNER_ROLE, _msgSender());
        manager = manager_;
    }

    function setManager(address manager_) public {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "MTRV1: Caller is not a default admin");
        manager = manager_;
    }

    function mint(address to, uint256 tokenId_) external  {
        // Check that the calling account has the minter role
        require(hasRole(MINTER_ROLE, msg.sender), "MTRV1: Caller is not a minter");
        _mint(to, tokenId_);
    }

    function burn(uint256 tokenId_) external {
        require(hasRole(BURNER_ROLE, _msgSender()), "MTRV1: must have burner role to burn");

        _burn(tokenId_);
    }

    function burnFromVault(uint vaultId_) external{
        require(INetworkState(manager).getVault(vaultId_)  == _msgSender(), "MTRV1: Caller is not vault");
        _burn(vaultId_);
    }

    function exists(uint256 tokenId_) external view  returns (bool) {
        return _exists(tokenId_);
    }
}

contracts/safu/NetworkState.sol.content:
pragma solidity ^0.8.17;
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {NetworkStateLibrary} from "./libraries/NetworkStateLibrary.sol";
import {IWETH} from "./interfaces/IWETH.sol";
import {TransferHelper} from "./libraries/TransferHelper.sol";
import {IERC721Minimal} from "./interfaces/IERC721Minimal.sol";
import {IBond} from "./interfaces/IBond.sol";
import {IERC20Minimal} from "./interfaces/IERC20Minimal.sol";

contract NetworkState is Initializable {
    using NetworkStateLibrary for NetworkStateLibrary.State;
    using NetworkStateLibrary for NetworkStateLibrary.CDP;

    NetworkStateLibrary.State private state;

    // CDP set events
    event CDPSet(
        address indexed collateral,
        uint32 mcr,
        uint32 rfr,
        uint32 lfr
    );

    // Bond state events
    event BondCreated(
        uint128 indexed id,
        address indexed collateral,
        uint256 cAmount,
        uint256 dAmount
    );

    event BondRedeemed(uint128 indexed id, uint256 indexed dAmount);

    event BondLiquidated(
        uint128 indexed id,
        address indexed collateral,
        uint256 cAmount,
        uint256 dAmount
    );

    // Bond CDP state events
    event DepositCollateral(uint128 id, uint256 amount);
    event WithdrawCollateral(uint128 id, uint256 amount);
    event BorrowMore(uint128 id, uint256 amount);
    event PayBackDebt(uint128 id, uint256 amount);

    // Bond errors
    error NotBondOwner(address caller, address owner);
    error InvalidAccess(address caller, address gov);

    function initialize(
        address coupon_,
        address currency_,
        address market_,
        address weth_
    ) external initializer {
        state._initialize(coupon_, currency_, market_, weth_);
    }

    function setCDP(
        address collateral_,
        uint32 mcr_,
        uint32 rfr_,
        uint32 lfr_
    ) external {
        state._setCDP(collateral_, mcr_, rfr_, lfr_);
    }

    function getCDP(
        address collateral_
    ) external view returns (NetworkStateLibrary.CDP memory cdp) {
        return state._getCDP(collateral_);
    }

    function borrow(
        address collateral_,
        uint cAmount_,
        uint dAmount_,
        uint128 id_
    ) external returns (address bond) {
        if (id_ == 0) {
            // create bond
            bond = state._createBond(collateral_, cAmount_, dAmount_);
            IERC20Minimal(state.currency).mint(msg.sender, dAmount_);
        } else {
            // borrow more from existing bond
            bond = state._predictAddress(collateral_, state.currency, id_);
            // TODO: get margin from bond and check if it exceeeds the amount
        }
    }

    function createBondETH(
        uint dAmount_
    ) public payable returns (address bond) {
        IWETH(state.weth).deposit{value: msg.value}();
        return state._createBond(state.weth, msg.value, dAmount_);
    }

    function isValidCDP(
        address collateral_,
        address debt_,
        uint256 cAmount_,
        uint256 dAmount_
    ) external view returns (bool) {
        return state._isValidCDP(collateral_, debt_, cAmount_, dAmount_);
    }

    function _takeFee(uint256 amount) internal {
        // state._takeFee();
    }

    modifier onlyBondOwner(uint256 id) {
        address owner = IERC721Minimal(state.coupon).ownerOf(id);
        if (owner != msg.sender) {
            revert NotBondOwner(msg.sender, owner);
        }
        _;
    }

    function depositCollateral(
        uint128 id,
        address collateral,
        uint256 amount
    ) public onlyBondOwner(id) returns (bool success) {
        address bond = state._predictAddress(collateral, state.currency, id);
        TransferHelper.safeTransferFrom(
            collateral,
            msg.sender,
            address(this),
            amount
        );
        TransferHelper.safeTransfer(collateral, bond, amount);
        //return state._depositCollateral(id, amount);
    }

    function depositCollateralETH(
        uint128 id,
        address collateral,
        uint256 amount
    ) external payable onlyBondOwner(id) returns (bool success) {
        address bond = state._predictAddress(collateral, state.currency, id);
        IWETH(state.weth).deposit{value: msg.value}();
        TransferHelper.safeTransfer(collateral, bond, amount);
    }

    function withdrawCollateral(
        uint128 id,
        address collateral,
        uint256 amount
    ) external onlyBondOwner(id) returns (bool success) {
        address bond = state._predictAddress(collateral, state.currency, id);
        IBond(bond).withdrawCollateral(amount);
        TransferHelper.safeTransfer(collateral, msg.sender, amount);
    }

    function borrowMore(
        uint128 id,
        address collateral,
        uint256 amount
    ) external onlyBondOwner(id) returns (bool success) {
        address bond = state._predictAddress(collateral, state.currency, id);
        return state._borrowMore(id, collateral, amount);
    }

    function payBackDebt(
        uint128 id,
        address collateral,
        uint256 amount
    ) external onlyBondOwner(id) returns (bool success) {
        address bond = state._predictAddress(collateral, state.currency, id);
        //return state._payBackDebt(bond, id, amount);
    }

    function liquidate(
        address collateral_,
        address debt_,
        uint128 id
    ) external onlyBondOwner(id) returns (bool success) {
        address bond = state._predictAddress(collateral_, state.currency, id);
        //return state._liquidate(collateral_, debt_, id);
    }

    function market() external view returns (address) {
        return state.market;
    }
}

