            address(token2),
            500000000,
            10,
            true,
            5,
            0,
            trader1
        );

        _showOrderbook(matchingEngine, address(token1), address(token2));

        for (uint256 i = 0; i < 10; i++) {
            vm.prank(trader1);
            matchingEngine.limitSell(
                address(token1),
                address(token2),
                500000000,
                i + 1,
                true,
                2,
                0,
                trader1
            );
        }

        // cancel order
        vm.prank(trader1);
        matchingEngine.cancelOrder(
            address(token1),
            address(token2),
            false,
            1,
            0
        );

        // recheck orders
        _showOrderbook(matchingEngine, address(token1), address(token2));

        // cancel order
        vm.prank(trader1);
        //vm.expectRevert();
        matchingEngine.cancelOrder(
            address(token1),
            address(token2),
            false,
            11,
            0
        );

        // recheck orders
        _showOrderbook(matchingEngine, address(token1), address(token2));

        // limit buy to check passing cancelled order
        vm.prank(trader1);
        matchingEngine.limitBuy(
            address(token1),
            address(token2),
            500000000,
            55,
            true,
            5,
            0,
            trader1
        );

        // recheck orders
        _showOrderbook(matchingEngine, address(token1), address(token2));
    }

