        uint256 afterTrader2T1Balance = token1.balanceOf(address(trader2));
        uint256 afterTrader1T2Balance = token2.balanceOf(address(trader1));
        uint256 afterTrader1T1Balance = token1.balanceOf(address(trader1));

        // differences
